mapper.select:
SELECT oid, oname, osubject, ostate, otime
FROM orderinfo
WHERE osubject = #{osubject}

SELECT oid, oname, osubject, ostate, otime
FROM orderinfo
WHERE oid = #{oid}

mapper.update:
UPDATE orderinfo

    oname = #{oname},
    osubject = #{osubject},
    ostate = #{ostate}

WHERE oid = #{oid}
